5642|/**
5643| * xsltApplyStripSpaces:
5644| * @ctxt:  a XSLT process context
5645| * @node:  the root of the XML tree
5646| *
5647| * Strip the unwanted ignorable spaces from the input tree
5648| */
5649|void
5650|xsltApplyStripSpaces(xsltTransformContextPtr ctxt, xmlNodePtr node) {
5651|    xmlNodePtr current;
5652|#ifdef WITH_XSLT_DEBUG_PROCESS
5653|    int nb = 0;
5654|#endif
5655|
5656|
5657|    current = node;
5658|    while (current != NULL) {
5659|	/*
5660|	 * Cleanup children empty nodes if asked for
5661|	 */
5662|	if ((IS_XSLT_REAL_NODE(current)) &&
5663|	    (current->children != NULL) &&
5664|	    (xsltFindElemSpaceHandling(ctxt, current))) {
5665|	    xmlNodePtr delete = NULL, cur = current->children;
5666|
5667|	    while (cur != NULL) {
5668|		if (IS_BLANK_NODE(cur))
5669|		    delete = cur;
5670|
5671|		cur = cur->next;
5672|		if (delete != NULL) {
5673|		    xmlUnlinkNode(delete);
5674|		    xmlFreeNode(delete);
5675|		    delete = NULL;
5676|#ifdef WITH_XSLT_DEBUG_PROCESS
5677|		    nb++;
5678|#endif
5679|		}
5680|	    }
5681|	}
5682|
5683|	/*
5684|	 * Skip to next node in document order.
5685|	 */
5686|	if (node->type == XML_ENTITY_REF_NODE) {
5687|	    /* process deep in entities */
5688|	    xsltApplyStripSpaces(ctxt, node->children);
5689|	}
5690|	if ((current->children != NULL) &&
5691|            (current->type != XML_ENTITY_REF_NODE)) {
5692|	    current = current->children;
5693|	} else if (current->next != NULL) {
5694|	    current = current->next;
5695|	} else {
5696|	    do {
5697|		current = current->parent;
5698|		if (current == NULL)
5699|		    break;
5700|		if (current == node)
5701|		    goto done;
5702|		if (current->next != NULL) {
5703|		    current = current->next;
5704|		    break;
5705|		}
5706|	    } while (current != NULL);
5707|	}
5708|    }
5709|
5710|done:
5711|#ifdef WITH_XSLT_DEBUG_PROCESS
5712|    XSLT_TRACE(ctxt,XSLT_TRACE_STRIP_SPACES,xsltGenericDebug(xsltGenericDebugContext,
5713|	     "xsltApplyStripSpaces: removed %d ignorable blank node\n", nb));
5714|#endif
5715|    return;
5716|}
