5718|static int
5719|xsltCountKeys(xsltTransformContextPtr ctxt)
5720|{
5721|    xsltStylesheetPtr style;
5722|    xsltKeyDefPtr keyd;
5723|
5724|    if (ctxt == NULL)
5725|	return(-1);
5726|
5727|    /*
5728|    * Do we have those nastly templates with a key() in the match pattern?
5729|    */
5730|    ctxt->hasTemplKeyPatterns = 0;
5731|    style = ctxt->style;
5732|    while (style != NULL) {
5733|	if (style->keyMatch != NULL) {
5734|	    ctxt->hasTemplKeyPatterns = 1;
5735|	    break;
5736|	}
5737|	style = xsltNextImport(style);
5738|    }
5739|    /*
5740|    * Count number of key declarations.
5741|    */
5742|    ctxt->nbKeys = 0;
5743|    style = ctxt->style;
5744|    while (style != NULL) {
5745|	keyd = style->keys;
5746|	while (keyd) {
5747|	    ctxt->nbKeys++;
5748|	    keyd = keyd->next;
5749|	}
5750|	style = xsltNextImport(style);
5751|    }
5752|    return(ctxt->nbKeys);
