5755|/**
5756| * xsltCleanupSourceDoc:
5757| * @doc:  Document
5758| *
5759| * Resets source node flags and ids stored in 'psvi' member.
5760| */
5761|void
5762|xsltCleanupSourceDoc(xmlDocPtr doc) {
5763|    xmlNodePtr cur = (xmlNodePtr) doc;
5764|    void **psviPtr;
5765|
5766|    while (1) {
5767|        xsltClearSourceNodeFlags(cur, XSLT_SOURCE_NODE_MASK);
5768|        psviPtr = xsltGetPSVIPtr(cur);
5769|        if (psviPtr)
5770|            *psviPtr = NULL;
5771|
5772|        if (cur->type == XML_ELEMENT_NODE) {
5773|            xmlAttrPtr prop = cur->properties;
5774|
5775|            while (prop) {
5776|                prop->atype &= ~(XSLT_SOURCE_NODE_MASK << 27);
5777|                prop->psvi = NULL;
5778|                prop = prop->next;
5779|            }
5780|        }
5781|
5782|        if (cur->children != NULL && cur->type != XML_ENTITY_REF_NODE) {
5783|            cur = cur->children;
5784|        } else {
5785|            if (cur == (xmlNodePtr) doc)
5786|                return;
5787|            while (cur->next == NULL) {
5788|                cur = cur->parent;
5789|                if (cur == (xmlNodePtr) doc)
5790|                    return;
5791|            }
5792|
5793|            cur = cur->next;
5794|        }
5795|    }
