 730|/**
 731| * xsltAddStackElem:
 732| * @ctxt:  xn XSLT transformation context
 733| * @elem:  a stack element
 734| *
 735| * Push an element (or list) onto the stack.
 736| * In case of a list, each member will be pushed into
 737| * a seperate slot; i.e. there's always 1 stack entry for
 738| * 1 stack element.
 739| *
 740| * Returns 0 in case of success, -1 in case of failure.
 741| */
 742|static int
 743|xsltAddStackElem(xsltTransformContextPtr ctxt, xsltStackElemPtr elem)
 744|{
 745|    if ((ctxt == NULL) || (elem == NULL))
 746|	return(-1);
 747|
 748|    do {
 749|	if (ctxt->varsNr >= ctxt->varsMax) {
 750|            xsltStackElemPtr *tmp;
 751|            int newMax = ctxt->varsMax == 0 ? 10 : 2 * ctxt->varsMax;
 752|
 753|            tmp = (xsltStackElemPtr *) xmlRealloc(ctxt->varsTab,
 754|                    newMax * sizeof(*tmp));
 755|            if (tmp == NULL) {
 756|                xmlGenericError(xmlGenericErrorContext, "realloc failed !\n");
 757|                return (-1);
 758|            }
 759|            ctxt->varsTab = tmp;
 760|            ctxt->varsMax = newMax;
 761|	}
 762|	ctxt->varsTab[ctxt->varsNr++] = elem;
 763|	ctxt->vars = elem;
 764|
